package edu.tum.cs.i1.pse.test;import static org.easymock.EasyMock.*;import static org.junit.Assert.*;import org.junit.Test;import edu.tum.cs.i1.pse.Order;import edu.tum.cs.i1.pse.OrderImpl;import edu.tum.cs.i1.pse.Warehouse;public class OrderInteractionTesterEasyMock {	private static String TALISKER = "Talisker";	private Warehouse warehouseMock;	@Test	public void fillingRemovesInventoryIfInStock() {		Order order = new OrderImpl(TALISKER, 50);		warehouseMock = createMock(Warehouse.class);		expect(warehouseMock.hasInventory(TALISKER, 50)).andReturn(true);		warehouseMock.remove(TALISKER, 50);		replay(warehouseMock);		order.fillOut(warehouseMock);		assertTrue(order.isFilled());		verify(warehouseMock);	}}